mangaToCount.reduce:
function(key, values, rereduce)
{
	if(rereduce)
	{
		total = 0;
		unread = 0;
		for(var i = 0; i < values.length; i++)
		{
			total += values[i]["total"];
			unread += values[i]["unread"];
		}
		return {"total":total, "unread":unread}
	}
	else
	{
		unread = 0;
		for(var i = 0; i < values.length; i++)
		{
			if(!values[i].isRead)
				unread += 1;
		}

		return {"total":values.length, "unread":unread}
	}
}

mangaToChapterList.reduce:
function(key, values, rereduce)
{
	if(rereduce)
	{
		return [].concat.apply([],values);
	}
	else
	{
		chapters = [];
		for(var i = 0; i < values.length; i++)
		{
			chapters.push(values[i]._id);
		}

		return chapters
	}
}
